import { Component, OnInit } from '@angular/core';
import { TodoService } from './todo.service';
import { ITodo } from './types/Todo';

export interface Todo {
  title: string;
  isComplete: boolean
}

 export class Todo implements Todo {
  constructor(public title: string, public isComplete: boolean){
  }
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {
  title = 'to-do';
  showEdit: boolean = false;
  todoToEdit!: Todo;

  todos: Todo[] = [];

  constructor(private todoService: TodoService){}

  ngOnInit() {
    this.todoService.getTodos();
  }

  addNewTodo(todo:string){
    let instance = new Todo(todo, false);
    this.todos.push(instance);
  }

  deleteTodo(todo:Todo){
    this.todos = this.todos.filter(item => todo.title !== item.title);
    this.showEdit = false;
  }

  editTodo(todo: Todo){
    this.showEdit = true;
    this.todoToEdit = todo;
  }

  cancelEdit(command: boolean){
    this.showEdit = false;
  }

  onEditTodo(data:any){
    this.todos = this.todos.map(x => x.title === data.oldTitle ? Object.assign({}, x, {title: data.newTitle, isComplete: false}) : x);
    this.showEdit = false;
  }

}
<div class="todo">

  <app-header (newItemEvent)="addNewTodo($event)" />

  <div class="todolist">
      <h3>List of Works Todo:</h3>
      <p class="error-info"></p>

      <app-todoitem *ngFor="let todo of todos" [todo]="todo" (outputTodo)="deleteTodo($event)" (editClick)="editTodo($event)" ></app-todoitem>     

  </div>
</div>

<app-popup *ngIf="showEdit" [todoToEdit]="todoToEdit" (cancelEdit)="cancelEdit($event)" (editData)="onEditTodo($event)" />
