import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { TodoService } from './todo.service';
import { ITodo, Todo } from './types/Todo';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {
  title = 'to-do';
  showEdit: boolean = false;
  todos: ITodo[] = [];
  todoToEdit!: ITodo;
  missingTitle: boolean = false;

  constructor(private todoService: TodoService){}

  ngOnInit() {
    this.todoService.getTodos().subscribe((todos => {
      this.todos = todos.slice(0, 5);
    }))
  }

  addNewTodo(title:string){
    if (title !== ''){
      this.missingTitle = false;
      this.todos.push(new Todo(title, false))
    } else {
      this.missingTitle = true;
    }
  }

  deleteTodo(todoToDelete:ITodo){
    this.todos = this.todos.filter(todo => todo.title !== todoToDelete.title);
  }

  editTodo(todoToEdit: ITodo){
    this.showEdit = true;
    this.todoToEdit = todoToEdit;
  }

  cancelEdit(value: boolean){
    this.showEdit = false;
  }

  titleToChange(data:any){
      this.todos = this.todos.map(todo => todo.title === data.oldTitle ? {...todo, title: data.newTitle} : {...todo});
      this.showEdit = false;
  }

}
<div class="todo">

  <app-header (newItemEvent)="addNewTodo($event)" />

  <div class="todolist">
      <h3>List of Works Todo:</h3>
      <p *ngIf="missingTitle" class="error-info">Cannot add todo without a title</p>

      <app-todoitem *ngFor="let todo of todos" [todo]="todo" (todoToDelete)="deleteTodo($event)" (todoToEdit)="editTodo($event)"></app-todoitem> 
  </div>
</div>

<app-popup *ngIf="showEdit" (cancelEdit)="cancelEdit($event)" [todoToEdit]="todoToEdit" (titleToChange)="titleToChange($event)" />
