import { Component, OnInit } from '@angular/core';
import { TodoService } from './todo.service';
import { ITodo } from './types/Todo';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit {
  title = 'to-do';
  showEdit: false;
  todos: ITodo[] = [];

  constructor(private todoService: TodoService){}

  ngOnInit() {
    this.todoService.getTodos().subscribe((todos => {
      this.todos = todos;
    }))
  }

}
<div class="todo">

  <!-- <app-header (newItemEvent)="addNewTodo($event)" /> -->
  <app-header />

  <div class="todolist">
      <h3>List of Works Todo:</h3>
      <p class="error-info"></p>

      <!-- <app-todoitem *ngFor="let todo of todos" [todo]="todo" (outputTodo)="deleteTodo($event)" (editClick)="editTodo($event)" ></app-todoitem>      -->

      <app-todoitem *ngFor="let todo of todos" [todo]="todo"></app-todoitem> 
  </div>
</div>

